mapper.update:
UPDATE tblboard SET
downcnt = downcnt + 1
WHERE bno = #{bno}

UPDATE tblboard SET
bno_step = bno_step + 1
WHERE bno_step >= #{bno_step}

UPDATE tblboard SET
bno_step = bno_step - 1
WHERE bno_step > #{bno_step}
